create table galaxytype (galaxytype_id int generated always as identity, name varchar(255) not null, description text,columncode varchar(10) unique, primary key(galaxytype_id));
create table galaxy (galaxy_id int generated always as identity, galaxytype_id int not null, name varchar(255) not null, description text,CountOfMoon int, lenght int,columncode varchar(10) unique, primary key(galaxy_id), constraint fk_galaxytype_id foreign key (galaxytype_id) references galaxytype (galaxytype_id));
create table star(star_id int generated always as identity, galaxy_id int not null, name varchar(255) not null, description text, totalcount int , size int, columncode varchar(10) uniqued,primary key(star_id), constraint fk_galaxy_id foreign key (galaxy_id) references galaxy(galaxy_id));
create table planet (planet_id int generated always as identity, star_id int not null, name varchar(255) not null, description text, has_life boolean, distance_from_earth numeric, is_spherical boolean, countofmoon  int,length int, columncode varchar(10) unique, primary key(planet_id), constraint fk_star_id foreign key (star_id) references star(star_id));
create table moon (moon_id int generated always as identity, planet_id int not null, name varchar(255),description text, is_spherical boolean, distinctofplanet int, columncode varchar(10) unique, primary key(moon_id),constraint fk_planet_id foreign key(planet_id) references planet (planet_id));
